(function(){
	'use strict';
	angular.module('fancygrid-angularjs', []).directive('fancygrid', [function(){
		return {
			restrict: 'EA',
			scope: {
				id: '@',
				fgConfig: '='
			},
			link : function($scope, $element, $attrs){
				if($scope.fgConfig){
					var config = $scope.fgConfig;
					config.renderTo = $scope.id;
					config.width = (angular.isUndefined(config.width) || config.width ===null)?$element.parent().width():config.width;
					config.height = (angular.isUndefined(config.height) || config.height ===null)?600:config.height;
					config.i18n = (angular.isUndefined(config.i18n) || config.i18n ===null)?'zh-CN':config.i18n;
					config.theme = (angular.isUndefined(config.theme) || config.theme ===null)?'gray':config.theme;
					config.paging = (angular.isUndefined(config.paging) || config.paging ===null)?true:config.paging;
					new FancyGrid(config);
				}
			}
		};
	}]);
})();
